DROP TABLE IF EXISTS `t_volunteers_info`;
CREATE TABLE `t_volunteers_info`  (
  `VOLUNTEERS_ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '志愿者id（注册时获取）',
  `VOLUNTEERS_USERNAME` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者用户名（注册时获取）',
  `VOLUNTEERS_EMAIL` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者邮箱（注册时获取）',
  `VOLUNTEERS_PHONE` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者手机号（注册时获取）',
  `VOLUNTEERS_PASSWORD` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者密码（注册时获取）',
  `VOLUNTEERS_POLITICAL` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者政治面貌（注册时获取）',
  `VOLUNTEERS_PROFESSIONAL` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者从业情况（注册时获取）',
  `VOLUNTEERS_ISAPPLYTEAM` tinyint(1) NOT NULL DEFAULT 0 COMMENT '标识该志愿者是否正在申请一个志愿者团队',
  `VOLUNTEERS_STATUS` tinyint(4) NOT NULL DEFAULT 1 COMMENT '志愿者账号状态（由管理员决定是否启用该账号 1-启用 0-不启用）',
  `VOLUNTEERS_DEGREE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者学历（注册时填写）',
  `VOLUNTEERS_ADDRESS` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者联系地址（注册时获取）',
  `VOLUNTEERS_PROVINCE` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者所属省份（注册时解析获得）',
  `VOLUNTEERS_AVATAR` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '志愿者头像地址（注册时后端给予默认头像地址）',
  `VOLUNTEERS_REGISTER` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '注册时间（注册时后端赋予）',
  `VOLUNTEERS_BIRTH` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者年龄（实名认证获取）',
  `VOLUNTEERS_SIMPLE` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '志愿者个人简介（自愿填写）',
  `VOLUNTEERS_NAME` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者姓名（实名认证获取）',
  `VOLUNTEERS_GENDER` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者性别（实名认证获取）',
  `VOLUNTEERS_NATIONAL` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者民族（实名认证获取）',
  `VOLUNTEERS_NATIVE` varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者籍贯（实名认证获取）',
  `VOLUNTEERS_CARD` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '志愿者身份证号（实名认证获取）',
  `VOLUNTEERS_HOURS` int(11) NOT NULL DEFAULT 0 COMMENT '志愿时长（所有申请的志愿活动时长之和）',
  `VOLUNTEERS_ACTIVECOUNT` int(11) NOT NULL DEFAULT 0 COMMENT '志愿者参与的志愿活动数量',
  `VOLUNTEERS_BREAK` int(11) NOT NULL DEFAULT 0 COMMENT '违约次数（申请志愿活动违约次数）',
  `VOLUNTEERS_TYPE` tinyint(4) NOT NULL DEFAULT 0 COMMENT '用户类型标识（用于区别用户类型）',
  `FK_VOLUNTEERS_TEAM` int(11) NULL DEFAULT NULL COMMENT '志愿者所属志愿者团队（加入志愿者团队获取）',
  PRIMARY KEY (`VOLUNTEERS_ID`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 11 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '志愿者信息表' ROW_FORMAT = Dynamic;

